import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'

class ReportSubmitBody extends Component {
    state = {studentKey:1, reportKey:-1, file:null}

    //get the report key
    getReportKey = (event) => {
        const reportKey = parseFloat(event.target.value)
        this.setState({reportKey})
    }

    //get the file
    getFile = (event) => {
        const file = event.target.files[0];
        console.log(file);
        this.setState({file})

    }

    //judge the input infomation and choose whether to submit
    handleSubmit = () => {
        if(this.state.reportKey === -1) {
            alert("Please chooes the report name!")
        }
        else if(this.state.file === null) {
            alert("Please upload the file!")
        }
        else{
            this.props.history.push('/student/report/success')
        }
    }


    render() {
        const reports = [{name:"report 1", key: 1}, {name:"report 2", key: 2}, {name:"reprot 3", key:3}];
        return (
            <div>
                <section className="contact-clean" onChange = {(e) => this.getReportKey(e)}>   
                    
                    <h2 className="text-center">Report Submission</h2>
                    <div className="mb-3">
                        <div className="w-100"></div><small className="form-text">Report Name</small>
                        <select className="form-select">
                            <optgroup label="Choose the report">
                            <option value = "-1">==Please choose==</option>
                                {reports.map((reportObj,index) => {
                                    return (
                                        <option value ={reportObj.key} key ={reportObj.key}>{reportObj.name}</option>
                                    )
                                })}
                            </optgroup>
                        </select>
                    </div>
                    <div className="mb-3"><small className="form-text">File Upload</small><input className="form-control" type="file" onChange = {(e) => this.getFile(e)}/></div>
                    <div className="mb-3"><button className="btn btn-primary" onClick = {() => this.handleSubmit()}>submit and sign</button></div>
                    
                </section>
            </div>
        )
    }
}

export default withRouter(ReportSubmitBody)
