import React, { Component } from 'react'
import { Link } from 'react-router-dom'

export default class ReportSubmitBody extends Component {
    getValue = (event) => {
        console.log(event.target.value)
    }
    render() {
        const reports = [{name:"report 1", key: 1}, {name:"report 2", key: 2}, {name:"reprot 3", key:3}];
        return (
            <div>
                <section className="contact-clean" onChange = {(e) => this.getValue(e)}>   
                    <form method="post">
                        <h2 className="text-center">Report Submission</h2>
                        <div className="mb-3">
                            <div className="w-100"></div><small className="form-text">Report Name</small>
                            <select className="form-select">
                                <optgroup label="Choose the report">
                                    {reports.map((reportObj,index) => {
                                        return (
                                            <option value ={reportObj.key} key ={reportObj.key}>{reportObj.name}</option>
                                        )
                                    })}
                                </optgroup>
                            </select>
                        </div>
                        <div className="mb-3"><small className="form-text">File Upload</small><input className="form-control" type="file"/></div>
                        <div className="mb-3"><Link className="btn btn-primary" role="button" to='/student/report/success'>submit and sign</Link></div>
                    </form>
                </section>
            </div>
        )
    }
}
